<template>
  <div>
    <div class="list_topSearch_header">
      <router-link to="/home"><van-icon name="arrow-left" class="arrow-left"></van-icon></router-link>
      <input type="text" placeholder="家居家装 好物低至5折" @click="backHome"/>
      <van-icon name="search" class="search" />
      <img
        src="data:image/png;base64,"
        @click="toggle"
      />
      <DropDownTab v-show="tab"></DropDownTab>
    </div>
  </div>
</template>

<script>
import Vue from "vue";
import { Icon } from "vant";
import DropDownTab from "../pulic/DropDownTab";
Vue.use(Icon);
export default {
  name: "topSearch",
  data() {
    return {
      tab: false
    };
  },
  components: {
    DropDownTab
  },
  methods: {
    toggle() {
      this.tab = !this.tab;
    },
    backHome () {
      this.$router.push('/search')
    }
  },
  mounted() {
    document.addEventListener("click", e => {
      if (!this.$el.contains(e.target)) {
        this.tab = false;
      }
    });
  }
};
</script>

